if_example:
x = 10
if x > 5:
    print("x is greater than 5")

elif_example:
x = 10
if x > 10:
    print("x is greater than 10")
elif x == 10:
    print("x is 10")

else_example:
x = 10
if x > 10:
    print("x is greater than 10")
else:
    print("x is less than or equal to 10")

break_example:
for i in range(5):
    if i == 3:
        break
    print(i)

continue_example:
for i in range(5):
    if i == 3:
        continue
    print(i)
